        const res = await axios({
            method:'POST',
            url:'http://127.0.0.1:8000/api/v1/task',
            data:{
                content,
                endDate
            }
        });
        console.log(res);
        // if(res.data.status=='ok'){
        //     const el=`<div class="task-card today-card">
        //     <p>${res.data.data.task.content}</p>
